package cache

import (
	"testing"
	"time"

	"github.com/stretchr/testify/assert"

	"github.com/zhufuyi/sponge/pkg/gotest"
	"github.com/zhufuyi/sponge/pkg/utils"

	"git.abanppc.com/farin-project/crud/internal/model"
)

func newModulesCache() *gotest.Cache {
	record1 := &model.Modules{}
	record1.ID = 1
	record2 := &model.Modules{}
	record2.ID = 2
	testData := map[string]interface{}{
		utils.Uint64ToStr(record1.ID): record1,
		utils.Uint64ToStr(record2.ID): record2,
	}

	c := gotest.NewCache(testData)
	c.ICache = NewModulesCache(&model.CacheType{
		CType: "redis",
		Rdb:   c.RedisClient,
	})
	return c
}

func Test_modulesCache_Set(t *testing.T) {
	c := newModulesCache()
	defer c.Close()

	record := c.TestDataSlice[0].(*model.Modules)
	err := c.ICache.(ModulesCache).Set(c.Ctx, record.ID, record, time.Hour)
	if err != nil {
		t.Fatal(err)
	}

	// nil data
	err = c.ICache.(ModulesCache).Set(c.Ctx, 0, nil, time.Hour)
	assert.NoError(t, err)
}

func Test_modulesCache_Get(t *testing.T) {
	c := newModulesCache()
	defer c.Close()

	record := c.TestDataSlice[0].(*model.Modules)
	err := c.ICache.(ModulesCache).Set(c.Ctx, record.ID, record, time.Hour)
	if err != nil {
		t.Fatal(err)
	}

	got, err := c.ICache.(ModulesCache).Get(c.Ctx, record.ID)
	if err != nil {
		t.Fatal(err)
	}
	assert.Equal(t, record, got)

	// zero key error
	_, err = c.ICache.(ModulesCache).Get(c.Ctx, 0)
	assert.Error(t, err)
}

func Test_modulesCache_MultiGet(t *testing.T) {
	c := newModulesCache()
	defer c.Close()

	var testData []*model.Modules
	for _, data := range c.TestDataSlice {
		testData = append(testData, data.(*model.Modules))
	}

	err := c.ICache.(ModulesCache).MultiSet(c.Ctx, testData, time.Hour)
	if err != nil {
		t.Fatal(err)
	}

	got, err := c.ICache.(ModulesCache).MultiGet(c.Ctx, c.GetIDs())
	if err != nil {
		t.Fatal(err)
	}

	expected := c.GetTestData()
	for k, v := range expected {
		assert.Equal(t, got[utils.StrToUint64(k)], v.(*model.Modules))
	}
}

