func TestNewModulesCache(t *testing.T) {
	c := NewModulesCache(&model.CacheType{
		CType: "",
	})
	assert.Nil(t, c)
	c = NewModulesCache(&model.CacheType{
		CType: "memory",
	})
	assert.NotNil(t, c)
	c = NewModulesCache(&model.CacheType{
		CType: "redis",
	})
	assert.NotNil(t, c)
}
